import React, { useEffect, useState } from "react";
import '../../App.css';
import '../createLotPopup/createLotPopup.css';
import closeIcon from '../../assets/images/close-icon.svg';
import CustomBtn from "../customBtn/customBtn";
import ProgressBar from "../progressBar/progressBar";
import './selectionPopup.css';
import SearchInput from "../searchInput/searchInput";
import PopupTabs from "../popupTabs/popupTabs";

const SelectionPopup = (props) => {

    const [progressStep, setProgressStep] = useState<number>(1);
    const [searchKeyword, setSearchKeyword] = useState<string>('');
    const tabs:TabsInterface[] = [
        {id: 1, label: 'Crypto'}, 
        {id: 2, label: 'Indexes'},
        {id: 3, label: 'Stocks'},
        {id: 4, label: 'Commodities'},
        {id: 5, label: 'Forex'}
    ];
    const [selectedTab, setSelectedTab] = useState<TabsInterface>(tabs[0]);

    const onNextClick = () => {
        if(progressStep === 3){
            props.setShowSelectionPopup(false);
        }
        else if(progressStep === 1){
            setProgressStep(2);
        }
        else if(progressStep === 2){
            setProgressStep(3);
        }
    }

    const onBackClick = () => {
        if(progressStep === 3){
            setProgressStep(2);
        }
        else if(progressStep === 2){
            setProgressStep(1);
        }        
    }

    const onSearchInput = (e) => {
        setSearchKeyword(e.target.value);
    }

    const onTabClick = () => {

    }

    return(
        <div className="popup-outer-container">
            <div className="popup-inner-container">
                <div className="display_flex space_between flex_align_center">
                    <p className="title-text selection-popup-title">
                        Multi-user Lot
                    </p>                
                    <img className="help-icon" src={closeIcon} onClick={() => props.setShowSelectionPopup(false)}/>
                </div>
                <br />
                <ProgressBar progressStep={progressStep}/>
                <br />
                <p className="selection-popup-subtitle">
                    {progressStep == 1 ? 'Choose your Asset' : 'Choose Opposing Asset'}
                </p>
                <br />
                <SearchInput 
                placeholder="Search Token eg. ETH, Gold etc"
                value={searchKeyword}
                onChange={onSearchInput}
                />
                <br />
                <PopupTabs
                selectedTab={selectedTab}
                tabsList={tabs}
                onTabClick={onTabClick}
                />
                <br />
                {progressStep == 1 ? 
                    <CustomBtn 
                    label="Next"
                    onClick={onNextClick}
                    />
                : 
                <div className="display_flex space_between flex_align_center">
                    <div className="flex-btn-container-child margin_right">
                        <CustomBtn 
                        label="Back"
                        onClick={onBackClick}
                        transparentBtn={true}
                        />
                    </div>
                    <div className="flex-btn-container-child margin_left">
                        <CustomBtn
                        label="Next"
                        onClick={onNextClick}
                        />
                    </div>
                </div>
                }                
            </div>            
        </div>
    );
}

export default SelectionPopup;
